{
    "name": "Visibility Settings",
    "reportType": "standard",
    "visibility": "public",
    "withMap": true,
    "filterExpression": {
        "common": {
            "positionSelector": "last",
            "positionTypeSelector": "positions",
            "xValue": 10
        },
        "assets": [],
        "vms": {},
        "areas": []
    },
    "mapConfiguration": {
        "visibilitySettings": {
            "positions": {
                "table": {
                    "values": ["fs"],
                    "order": ["fs", "extMark", "ircs", "cfr", "posTime", "lon", "lat", "stat", "m_spd", "c_spd", "crs", "msg_tp", "act_tp", "source", "name"]
                },
                "popup": {
                    "values": ["fs"],
                    "order": ["fs", "extMark", "ircs", "cfr", "posTime", "crs", "c_spd", "m_spd", "stat", "lat", "lon", "msg_tp", "act_tp", "source", "name"],
                    "isAttributeVisible": true
                },
                "labels": {
                    "values": ["fs"],
                    "order": ["fs", "extMark", "ircs", "cfr", "posTime", "lon", "lat", "stat", "m_spd", "c_spd", "crs", "msg_tp", "act_tp", "source", "name"],
                    "isAttributeVisible": true
                }
            },
            "segments": {
                "table": {
                    "values": ["extMark"],
                    "order": ["extMark", "ircs", "cfr", "dist", "cat", "crs", "spd", "dur", "fs", "name"]
                },
                "popup": {
                    "values": ["extMark"],
                    "order": ["fs", "extMark", "ircs", "cfr", "dist", "dur", "spd", "crs", "cat", "name"],
                    "isAttributeVisible": true
                },
                "labels": {
                    "values": ["ircs"],
                    "order": ["extMark", "ircs", "cfr", "dist", "cat", "crs", "spd", "dur", "fs", "name"],
                    "isAttributeVisible": true
                }
            },
            "tracks": {
                "table": {
                    "values": ["dur"],
                    "order": ["fs", "extMark", "ircs", "cfr", "name", "dist", "dur", "timeSea"]
                }
            }
        }
    }
}

